import React, { Component } from "react";
import axios from "axios";

export default class EditShow extends Component {
  constructor(props) {
    super(props);

    this.onChangeUsername = this.onChangeUsername.bind(this);
    this.onChangeName = this.onChangeName.bind(this);
    this.onChangeDescription = this.onChangeDescription.bind(this);
    this.onChangeGenre = this.onChangeGenre.bind(this);
    this.onChangeRating = this.onChangeRating.bind(this);
    this.onSubmit = this.onSubmit.bind(this);

    this.state = {
      username: "",
      name: "",
      description: "",
      genre: "",
      rating: 0,
      users: [],
    };
  }

  componentDidMount() {
    axios
      .get("http://localhost:5000/shows/" + this.props.match.params.id)
      .then((response) => {
        this.setState({
          username: response.data.username,
          description: response.data.description,
          name: response.data.name,
          rating: response.data.rating,
          genre: response.data.genre,
        });
      })
      .catch(function (error) {
        console.log(error);
      });

    axios
      .get("http://localhost:5000/users/")
      .then((response) => {
        if (response.data.length > 0) {
          this.setState({
            users: response.data.map((user) => user.username),
          });
        }
      })
      .catch((error) => {
        console.log(error);
      });
  }

  onChangeUsername(e) {
    this.setState({
      username: e.target.value,
    });
  }

  onChangeDescription(e) {
    this.setState({
      description: e.target.value,
    });
  }

  onChangeGenre(e) {
    this.setState({
      genre: e.target.value,
    });
  }

  onChangeName(e) {
    this.setState({
      name: e.target.value,
    });
  }

  onChangeRating(e) {
    this.setState({
      rating: e.target.value,
    });
  }

  onSubmit(e) {
    e.preventDefault();

    const show = {
      username: this.state.username,
      description: this.state.description,
      name: this.state.name,
      rating: this.state.rating,
      genre: this.state.genre,
    };

    console.log(show);

    axios
      .post(
        "http://localhost:5000/shows/update/" + this.props.match.params.id,
        show
      )
      .then((res) => console.log(res.data));

    window.location = "/";
  }

  render() {
    return (
      <div class="pa4 f3 w-100 mw8 center avenir">
        <h3>Edit shows</h3>
        <table>
          <form onSubmit={this.onSubmit}>
            <div className="form-group">
              <tr>
                <td>
                  <label>Vouched by: </label>
                </td>
                <td>
                  {" "}
                  <select
                    ref="userInput"
                    required
                    className="form-control"
                    value={this.state.username}
                    onChange={this.onChangeUsername}
                  >
                    {this.state.users.map(function (user) {
                      return (
                        <option key={user} value={user}>
                          {user}
                        </option>
                      );
                    })}
                  </select>
                </td>
              </tr>
            </div>

            <div className="form-group">
              <tr>
                <td>
                  <label>Show: </label>
                </td>
                <td>
                  <input
                    type="text"
                    required
                    className="form-control"
                    value={this.state.name}
                    onChange={this.onChangeName}
                  />
                </td>
              </tr>
            </div>

            <div className="form-group">
              <tr>
                <td>
                  <label>Description: </label>
                </td>
                <td>
                  <input
                    type="text"
                    required
                    className="form-control"
                    value={this.state.description}
                    onChange={this.onChangeDescription}
                  />
                </td>
              </tr>
            </div>
            <div className="form-group">
              <label>Genre: </label>
              <input
                type="text"
                required
                className="form-control"
                value={this.state.genre}
                onChange={this.onChangeGenre}
              />
            </div>
            <div className="form-group">
              <label>Rating (out of 10) </label>
              <input
                type="text"
                className="form-control"
                value={this.state.rating}
                onChange={this.onChangeRating}
              />
            </div>

            <div className="form-group">
              <input
                type="submit"
                value="Edit Show"
                className="btn btn-primary"
              />
            </div>
          </form>
        </table>
      </div>
    );
  }
}
